import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

export async function generatePDF() {
  const formElement = document.getElementById('form-to-pdf');
  if (!formElement) {
    console.error('generatePDF error: form element not found');
    return;
  }

  const canvas = await html2canvas(formElement, {
    scale: window.devicePixelRatio ? window.devicePixelRatio * 1.5 : 2,
    useCORS: true,
    letterRendering: true,
    onclone: (clonedDoc) => {
        const clonedForm = clonedDoc.getElementById('form-to-pdf');
        if (!clonedForm) return;
        
        clonedForm.classList.add('print-form');
        
        // for each textarea, bump its rows to match its content
        clonedForm.querySelectorAll('textarea').forEach((ta) => {
            const lines = ta.value.split(/\r\n|\n|\r/).length;
            const minRows = parseInt(ta.getAttribute('rows') || '4', 10);
            ta.setAttribute('rows', Math.max(lines, minRows));
        });
        }
  });

  const imgData = canvas.toDataURL('image/png');
  const pdf = new jsPDF('p', 'mm', 'a4');
  const pdfWidth = pdf.internal.pageSize.getWidth();
  const pdfHeight = (canvas.height * pdfWidth) / canvas.width;
  pdf.addImage(imgData, 'PNG', 0, 0, pdfWidth, pdfHeight);
  pdf.save('form.pdf');
}
