                      </div>
                      <div>
                        <input
                          type="checkbox"
                          id="CDW_OBIEE"
                          name="CDW_OBIEE"
                          checked={formData.CDW_OBIEE || false}
                          onChange={handleInputChange}
                        />
                        <label htmlFor="CDW_OBIEE">CDW/OBIEE</label>
                      </div>
                      <div>
                        <input
                          type="checkbox"
                          id="other_access"
                          name="other_access"
                          checked={formData.other_access || false}
                          onChange={handleInputChange}
                        />
