                    <input
                      type="radio"
                      id="ta_transfer_yes"
                      name="ta_transfer"
                      value="yes"
                      checked={formData.ta_transfer === 'yes'}
                      onChange={handleInputChange}
                    />
                    <label htmlFor="ta_transfer_yes">Yes</label>
                  </div>
                  <div>
                    <input
                      type="radio"
                      id="ta_transfer_no"
                      name="ta_transfer"
                      value="no"
                      checked={formData.ta_transfer === 'no'}
                      onChange={handleInputChange}
                    />
                    <label htmlFor="ta_transfer_no">No</label>
                  </div>
                </div>                
