                  <>
                    <div className="date-field">
                    <label htmlFor="return_date">
                      Employee Return Date: 
                    </label>
                    <input
                      type="date"
                      id="return_date"
                      name="return_date"
                      value={formData.return_date || ''}
                      onChange={handleInputChange}
                    />
                  </div>
                  </>
                )}
              </>
            )}

