import React from 'react';
import Tippy from '@tippyjs/react';
import './RequestTypeButtons.css';

const RequestTypeButtons = ({ selected, onSelect }) => {
  const requestTypes = [
    "Information Change",
    "Access Request",
    "Temporary Appointment or Acting Opportunities",
    "Change of Hours",
    "Position Movement",
    "Leave - Departing/Returning",
    "Transfer to Another Ministry",
    "Resignation, Retirement, or Termination"
  ];

  return (
    <div className="request-type-buttons">
      {requestTypes.map((type) => (
        <Tippy
          key={type}
          content={
            type === "Access Request"
              ? "Access to drives, folders, mailboxes, ICE, Teleopti, I-Expense, Signing/Expense Authority, etc."
              : ""
          }
          delay={[0, 0]} // [showDelay, hideDelay] in milliseconds
        >
          <button
            type="button"
            className={`request-type-button ${selected === type ? 'selected' : ''}`}
            onClick={() => onSelect(type)}
          >
            {type}
          </button>
        </Tippy>
      ))}
    </div>
  );
};

export default RequestTypeButtons;
